sys_variable_value.value:
(function execute(inputs, outputs) {
  var userRecords = [];

  while(inputs.userGroupRecords.next()) {
    gs.error("1. Next into User Group Record: " + inputs.userGroupRecords.sys_id);
    gs.error("1.1 Processing " + inputs.userGroupRecords.user.name + " record. ");
    if(inputs.userGroupRecords.user.sys_id.toString() !== inputs.userSysIDToExclude.toString()) {
      gs.error("1.2 Processing " + inputs.userGroupRecords.user.name.toString() + " record - Doesnt match the Exclude ID. GroupUserSysID = " + inputs.userGroupRecords.user.sys_id.toString() + ". UserSysID to Exclude = "+ inputs.userSysIDToExclude.toString());
      userRecords.push(inputs.userGroupRecords.user.sys_id.toString());
      gs.error("1.3 Pushed new value of " + inputs.userGroupRecords.user.sys_id.toString() + " into array. Array is now " + userRecords.toString());
    } else {
      gs.error("2. User group User Sys ID matches the User Sys ID to exclude" + inputs.userGroupRecords.user.name.toString());
    }
  }
  gs.error("2.1 userRecords is " + userRecords.toString());
  var mathFloor = Math.floor(Math.random()*userRecords.length);
  gs.error("3. Selecting Index of: " + mathFloor.toString());
  var selectedUserSysID = userRecords[mathFloor];
  gs.error("5. Selected User Sys ID is " + selectedUserSysID);
  var selectedUser = new GlideRecord('sys_user');
  selectedUser.get(selectedUserSysID);
  gs.error("6. Selected User name is " + selectedUser.user_name);

  outputs.selecteduser = selectedUser;
})(inputs, outputs);

